import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { getToken, logout } from "../utils/authStorage";
import "./Subscription.css";

const BASE_URL = "http://localhost:5000/api/v1";

const Subscription = () => {
  const navigate = useNavigate();

  const [acctNo, setAcctNo] = useState("");
  const [acctName, setAcctName] = useState("");
  const [branch, setBranch] = useState("");
  const [threshold, setThreshold] = useState("");
  const [statusMessage, setStatusMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const [verified, setVerified] = useState(false);
  const [subscribed, setSubscribed] = useState(false);

  const [notifySMS, setNotifySMS] = useState(false);
  const [notifyEmail, setNotifyEmail] = useState(false);
  const [notifyRM, setNotifyRM] = useState(true);

  /* ----------------------------------
     Central API Handler
  ---------------------------------- */
  const apiRequest = async (endpoint, options = {}) => {
    const token = getToken();

    if (!token) {
      logout();
      navigate("/");
      return;
    }

    const response = await fetch(`${BASE_URL}${endpoint}`, {
      ...options,
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
    });

    const result = await response.json();

    if (result.message === "Token has expired") {
      alert("Session expired. Please login again.");
      logout();
      navigate("/");
      return;
    }

    return result;
  };

  /* ----------------------------------
     VERIFY ACCOUNT
  ---------------------------------- */
  const verifyAccount = async () => {
    if (!acctNo) {
      setStatusMessage("Enter account number.");
      return;
    }

    try {
      setLoading(true);
      setStatusMessage("Verifying...");

      const result = await apiRequest(`/customers/${acctNo}`);

      if (!result?.success) throw new Error(result?.message);

      setAcctName(result.data.AC_DESC);
      setBranch(result.data.BRANCH_CODE);
      setVerified(true);
      setStatusMessage("Account verified.");
    } catch (err) {
      setStatusMessage(err.message);
      setVerified(false);
    } finally {
      setLoading(false);
    }
  };

  /* ----------------------------------
     SUBSCRIBE / UPDATE
  ---------------------------------- */
  const handleSubscribeOrUpdate = async (action) => {
    if (!verified) {
      setStatusMessage("Verify account first.");
      return;
    }

    if (!threshold) {
      setStatusMessage("Enter reorder threshold.");
      return;
    }

    try {
      setLoading(true);
      setStatusMessage(
        action === "subscribe" ? "Subscribing..." : "Updating..."
      );

      const endpoint =
        action === "subscribe"
          ? "/subscriptions/subscribe"
          : `/subscriptions/${acctNo}`;

      const method = action === "subscribe" ? "POST" : "PUT";

      const result = await apiRequest(endpoint, {
        method,
        body: JSON.stringify({
          branch_code: branch,
          reorder_threshold_number: Number(threshold),
          subscribed: "Y",
          notify_sms: notifySMS ? "Y" : "N",
          notify_email: notifyEmail ? "Y" : "N",
          notify_rm: notifyRM ? "Y" : "N",
        }),
      });

      if (!result?.success) throw new Error(result?.message);

      setSubscribed(true);
      setStatusMessage(
        action === "subscribe"
          ? "Subscription successful ✅"
          : "Update successful ✅"
      );
    } catch (err) {
      setStatusMessage(err.message);
    } finally {
      setLoading(false);
    }
  };

  /* ----------------------------------
     UNSUBSCRIBE
  ---------------------------------- */
  const handleUnsubscribe = async () => {
    if (!verified) {
      setStatusMessage("Verify account first.");
      return;
    }

    try {
      setLoading(true);
      setStatusMessage("Unsubscribing...");

      const result = await apiRequest(
        `/subscriptions/unsubscribe/${acctNo}`,
        { method: "POST" }
      );

      if (!result?.success) throw new Error(result?.message);

      setSubscribed(false);
      setStatusMessage("Unsubscribed successfully ✅");
    } catch (err) {
      setStatusMessage(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h1>Chequebook Reorder Alert Service</h1>

        <input
          type="text"
          placeholder="Account number"
          value={acctNo}
          onChange={(e) => setAcctNo(e.target.value)}
        />

        <button onClick={verifyAccount} disabled={loading}>
          Verify
        </button>

        <p>{statusMessage}</p>

        <input type="text" value={acctName} readOnly placeholder="Account Name" />
        <input type="text" value={branch} readOnly placeholder="Branch" />

        <input
          type="number"
          placeholder="Reorder Threshold"
          value={threshold}
          onChange={(e) => setThreshold(e.target.value)}
          disabled={!verified}
        />

        <div>
          <label>
            <input
              type="checkbox"
              checked={notifySMS}
              onChange={() => setNotifySMS(!notifySMS)}
            />
            SMS
          </label>

          <label>
            <input
              type="checkbox"
              checked={notifyEmail}
              onChange={() => setNotifyEmail(!notifyEmail)}
            />
            Email
          </label>

          <label>
            <input
              type="checkbox"
              checked={notifyRM}
              onChange={() => setNotifyRM(!notifyRM)}
            />
            RM
          </label>
        </div>

        {!subscribed ? (
          <button
            onClick={() => handleSubscribeOrUpdate("subscribe")}
            disabled={loading}
          >
            Subscribe
          </button>
        ) : (
          <>
            <button
              onClick={() => handleSubscribeOrUpdate("update")}
              disabled={loading}
            >
              Update
            </button>

            <button onClick={handleUnsubscribe} disabled={loading}>
              Unsubscribe
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default Subscription;
