import { useState } from "react";
import "./Subscription.css";
import omniLogo from "../assets/omni-logo.jpg";

export default function Subscription() {
  const [acctNo, setAcctNo] = useState("");
  const [acctName, setAcctName] = useState("");
  const [branch, setBranch] = useState("");
  const [threshold, setThreshold] = useState("");
  const [subscribed, setSubscribed] = useState(false);
  const [modal, setModal] = useState({ show: false, title: "", message: "" });

  function showModal(title, message) {
    setModal({ show: true, title, message });
  }

  function hideModal() {
    setModal({ ...modal, show: false });
  }

  function verifyAccount() {
    if (!acctNo) {
      showModal("Validation Error", "Please enter an account number");
      return;
    }

    // üîÅ MOCK (replace with backend later)
    setAcctName("Ugo Chukwu");
    setBranch("Lagos Main");
  }

  function subscribe() {
    if (!threshold) {
      showModal("Validation Error", "Enter threshold");
      return;
    }
    setSubscribed(true);
    showModal("Subscribed", "Subscription successful");
  }

  return (
    <div className="container">
      <div className="card">
        <header>
          <div className="logo">
            <img src={omniLogo} alt="Omni Logo" />
          </div>

          <h1>Chequebook Reorder Alert Service</h1>
        </header>

        <label>Account Number</label>
        <div className="accountverify">
          <input
            type="number"
            value={acctNo}
            onChange={e => setAcctNo(e.target.value)}
          />
          <button type="button" onClick={verifyAccount} className="btn-ghost">
            Verify
          </button>
        </div>

        <label>Account Name</label>
        <input value={acctName} readOnly />

        <label>Branch</label>
        <input value={branch} readOnly />

        <label>Reorder Threshold</label>
        <input
          type="number"
          value={threshold}
          onChange={e => setThreshold(e.target.value)}
        />

        <div className="actions">
          <button className="btn-primary" onClick={subscribe} disabled={subscribed}>
            Subscribe
          </button>
        </div>
      </div>

      {modal.show && (
        <div className="modal-backdrop">
          <div className="modal">
            <h3>{modal.title}</h3>
            <p>{modal.message}</p>
            <button onClick={hideModal} className="btn-primary">OK</button>
          </div>
        </div>
      )}
    </div>
  );
}
