import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { loginUser } from "../api/auth";
import { saveAuthData } from "../utils/authStorage";
import "./Login.css";

const Login = () => {
  const navigate = useNavigate();

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    if (!username || !password) {
      setError("Username and password are required");
      return;
    }

    try {
      setLoading(true);

      const data = await loginUser(username, password);

      // Save token + user info
      saveAuthData(data);

      // Optional: block inactive users
      if (data.USER_STATUS !== "A") {
        setError("User account is not active");
        return;
      }

      navigate("/subscription");
    } catch (err) {
      setError(err.message || "Login failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-page">
      {/* LEFT SIDE */}
      <div className="login-container">
        <div className="xnett-logo">
          <div className="logo">
            <img src="/omni_logo.png" alt="OmniBSIC Logo" />
          </div> 
        </div>
        

        <h1>Login</h1>
        <div className="login-desc">Enter your login credentials</div>

        <div className="login-form">
          <form className="form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Email Address</label>
              <input
                className="form-control"
                type="email"
                placeholder="Enter your email address"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>Password</label>
              <input
                className="form-control"
                type="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>

            <button className="btn-primary" type="submit">
              Login
            </button>
          </form>
        </div>

        <div className="footer-text">
          Â©2025 OmniBSIC Bank. Powered by Xnett. All rights reserved.
        </div>
      </div>

      {/* RIGHT SIDE IMAGE */}
      <div className="login-img" /> 
    </div>
  
  );
};

export default Login;
