import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Login.css";

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (email && password) {
      console.log("Login clicked", { email, password });
      navigate("/subscription");
    } else {
      alert("Please enter email and password");
    }
  };

  return (
    <div className="login-page">
      {/* LEFT SIDE */}
      <div className="login-container">
        <div className="xnett-logo">
          <div className="logo">
            <img 
            src="/public/omni_logo.jpg" 
            alt="OmniBSIC Bank Logo"
            width="48px"
            height="48px" />
          </div>
        </div>

        <h1>Login</h1>
        <div className="login-desc">Enter your login credentials</div>

        <div className="login-form">
          <form className="form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Email Address</label>
              <input
                className="form-control"
                type="email"
                placeholder="Enter your email address"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>Password</label>
              <input
                className="form-control"
                type="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>

            <button className="btn-primary" type="submit">
              Login
            </button>
          </form>
        </div>

        <div className="footer-text">
          Â©2025 OmniBSIC Bank. Powered by Xnett. All rights reserved.
        </div>
      </div>

      {/* RIGHT SIDE IMAGE */}
      <div className="login-img">
        <img
          src="/public/omni_photo.png"
          alt="Login Illustration"
          width="100%"
          height="100%"
        />
      </div>
    </div>
  );
};

export default Login;
