import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Subscription.css";

const BASE_URL = "http://localhost:5000/api/v1";

const Subscription = () => {
  const navigate = useNavigate();

  const [acctNo, setAcctNo] = useState("");
  const [acctName, setAcctName] = useState("");
  const [branch, setBranch] = useState("");
  const [threshold, setThreshold] = useState("");
  const [statusMessage, setStatusMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const [verified, setVerified] = useState(false);
  const [subscribed, setSubscribed] = useState(false);

  const [notifySMS, setNotifySMS] = useState(false);
  const [notifyEmail, setNotifyEmail] = useState(false);
  const [notifyRM, setNotifyRM] = useState(true);

  /* ----------------------------------
     GLOBAL TOKEN HANDLER
  ---------------------------------- */
  const handleTokenExpiry = (message) => {
    if (message === "Token has expired") {
      localStorage.clear();
      alert("Session expired. Please login again.");
      navigate("/");
      return true;
    }
    return false;
  };

  const getAuthHeaders = () => {
    const token = localStorage.getItem("token");
    return {
      "Content-Type": "application/json",
      Authorization: `Bearer ${token}`
    };
  };

  /* ----------------------------------
     VERIFY ACCOUNT
  ---------------------------------- */
  const verifyAccount = async () => {
    if (!acctNo) {
      setStatusMessage("Enter account number.");
      return;
    }

    try {
      setLoading(true);
      setStatusMessage("Verifying...");
      setVerified(false);

      const response = await fetch(
        `${BASE_URL}/customers/${acctNo}`,
        { headers: getAuthHeaders() }
      );

      const result = await response.json();

      if (handleTokenExpiry(result.message)) return;

      if (!response.ok || !result.success) {
        throw new Error(result.message);
      }

      setAcctName(result.data.AC_DESC);
      setBranch(result.data.BRANCH_CODE);
      setVerified(true);
      setStatusMessage("Account verified.");

    } catch (error) {
      setStatusMessage(error.message);
      setVerified(false);
    } finally {
      setLoading(false);
    }
  };

  /* ----------------------------------
     SUBSCRIBE
  ---------------------------------- */
  const handleSubscribe = async () => {
    try {
      setLoading(true);
      setStatusMessage("Subscribing...");

      const response = await fetch(
        `${BASE_URL}/subscriptions/subscribe`,
        {
          method: "POST",
          headers: getAuthHeaders(),
          body: JSON.stringify({
            cust_ac_no: acctNo,
            branch_code: branch,
            reorder_threshold_number: Number(threshold),
            subscribed: "Y",
            notify_sms: notifySMS ? "Y" : "N",
            notify_email: notifyEmail ? "Y" : "N",
            notify_rm: notifyRM ? "Y" : "N"
          })
        }
      );

      const result = await response.json();

      if (handleTokenExpiry(result.message)) return;

      if (!response.ok || !result.success) {
        throw new Error(result.message);
      }

      setSubscribed(true);
      setStatusMessage("Subscription successful ✅");

    } catch (error) {
      setStatusMessage(error.message);
    } finally {
      setLoading(false);
    }
  };

  /* ----------------------------------
     UPDATE SUBSCRIPTION
  ---------------------------------- */
  const handleUpdate = async () => {
    try {
      setLoading(true);
      setStatusMessage("Updating subscription...");

      const response = await fetch(
        `${BASE_URL}/subscriptions/${acctNo}`,
        {
          method: "PUT", // change if backend expects PATCH
          headers: getAuthHeaders(),
          body: JSON.stringify({
            branch_code: branch,
            reorder_threshold_number: Number(threshold),
            subscribed: "Y",
            notify_sms: notifySMS ? "Y" : "N",
            notify_email: notifyEmail ? "Y" : "N",
            notify_rm: notifyRM ? "Y" : "N"
          })
        }
      );

      const result = await response.json();

      if (handleTokenExpiry(result.message)) return;

      if (!response.ok || !result.success) {
        throw new Error(result.message);
      }

      setStatusMessage("Subscription updated successfully ✅");

    } catch (error) {
      setStatusMessage(error.message);
    } finally {
      setLoading(false);
    }
  };

  /* ----------------------------------
     UNSUBSCRIBE
  ---------------------------------- */
  const handleUnsubscribe = async () => {
    try {
      setLoading(true);
      setStatusMessage("Unsubscribing...");

      const response = await fetch(
        `${BASE_URL}/subscriptions/unsubscribe/${acctNo}`,
        {
          method: "POST", // change if DELETE
          headers: getAuthHeaders()
        }
      );

      const result = await response.json();

      if (handleTokenExpiry(result.message)) return;

      if (!response.ok || !result.success) {
        throw new Error(result.message);
      }

      setSubscribed(false);
      setStatusMessage("Customer unsubscribed successfully ✅");

    } catch (error) {
      setStatusMessage(error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h1>Chequebook Reorder Alert Service</h1>

        <label>Account Number</label>
        <input
          type="text"
          value={acctNo}
          onChange={(e) => setAcctNo(e.target.value)}
        />
        <button onClick={verifyAccount} disabled={loading}>
          Verify
        </button>

        <p>{statusMessage}</p>

        <input type="text" value={acctName} readOnly />
        <input type="text" value={branch} readOnly />

        <input
          type="number"
          value={threshold}
          onChange={(e) => setThreshold(e.target.value)}
          disabled={!verified}
        />

        <label>
          <input
            type="checkbox"
            checked={notifySMS}
            onChange={() => setNotifySMS(!notifySMS)}
          />
          SMS
        </label>

        <label>
          <input
            type="checkbox"
            checked={notifyEmail}
            onChange={() => setNotifyEmail(!notifyEmail)}
          />
          Email
        </label>

        <label>
          <input
            type="checkbox"
            checked={notifyRM}
            onChange={() => setNotifyRM(!notifyRM)}
          />
          RM
        </label>

        <div>
          {!subscribed ? (
            <button onClick={handleSubscribe} disabled={!verified}>
              Subscribe
            </button>
          ) : (
            <>
              <button onClick={handleUpdate}>Update</button>
              <button onClick={handleUnsubscribe}>Unsubscribe</button>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Subscription;
