import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { getToken, logout } from "../utils/authStorage";
import "./Subscription.css";

const BASE_URL = "http://localhost:5000/api/v1";

const Subscription = () => {
  const navigate = useNavigate();

  const [acctNo, setAcctNo] = useState("");
  const [acctName, setAcctName] = useState("");
  const [branch, setBranch] = useState("");
  const [threshold, setThreshold] = useState("");
  const [statusMessage, setStatusMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const [verified, setVerified] = useState(false);
  const [subscribed, setSubscribed] = useState(false);

  const [notifySMS, setNotifySMS] = useState(false);
  const [notifyEmail, setNotifyEmail] = useState(false);
  const [notifyRM, setNotifyRM] = useState(true);

  /* ----------------------------------
     Centralized API Call
  ---------------------------------- */
  const apiRequest = async (endpoint, options = {}) => {
    const token = getToken();
    if (!token) {
      logout();
      navigate("/");
      return;
    }

    const res = await fetch(`${BASE_URL}${endpoint}`, {
      ...options,
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
        ...options.headers
      }
    });

    const data = await res.json();

    if (data.message === "Token has expired") {
      alert("Session expired. Please login again.");
      logout();
      navigate("/");
      return;
    }

    return data;
  };

  /* ----------------------------------
     VERIFY ACCOUNT
  ---------------------------------- */
  const verifyAccount = async () => {
    if (!acctNo) {
      setStatusMessage("Enter account number.");
      return;
    }

    try {
      setLoading(true);
      setStatusMessage("Verifying...");

      const result = await apiRequest(`/customers/${acctNo}`);
      if (!result?.success) throw new Error(result?.message);

      setAcctName(result.data.AC_DESC);
      setBranch(result.data.BRANCH_CODE);
      setVerified(true);
      setStatusMessage("Account verified.");
    } catch (err) {
      setStatusMessage(err.message);
      setVerified(false);
    } finally {
      setLoading(false);
    }
  };

  /* ----------------------------------
     SUBSCRIBE / UPDATE / UNSUBSCRIBE
  ---------------------------------- */
  const handleSubscribeOrUpdate = async (action) => {
    if (!verified) {
      setStatusMessage("Verify account first.");
      return;
    }

    if (!threshold) {
      setStatusMessage("Enter reorder threshold.");
      return;
    }

    try {
      setLoading(true);
      setStatusMessage(action === "subscribe" ? "Subscribing..." : "Updating...");

      const method = action === "subscribe" ? "POST" : "PUT";
      const endpoint = action === "subscribe" ? "/subscriptions/subscribe" : `/subscriptions/${acctNo}`;

      const result = await apiRequest(endpoint, {
        method,
        body: JSON.stringify({
          branch_code: branch,
          reorder_threshold_number: Number(threshold),
          subscribed: "Y",
          notify_sms: notifySMS ? "Y" : "N",
          notify_email: notifyEmail ? "Y" : "N",
          notify_rm: notifyRM ? "Y" : "N"
        })
      });

      if (!result?.success) throw new Error(result?.message);

      setSubscribed(true);
      setStatusMessage(action === "subscribe" ? "Subscribed ✅" : "Updated ✅");

    } catch (err) {
      setStatusMessage(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleUnsubscribe = async () => {
    if (!verified) return setStatusMessage("Verify account first.");
    try {
      setLoading(true);
      setStatusMessage("Unsubscribing...");

      const result = await apiRequest(`/subscriptions/unsubscribe/${acctNo}`, { method: "POST" });
      if (!result?.success) throw new Error(result?.message);

      setSubscribed(false);
      setStatusMessage("Unsubscribed ✅");
    } catch (err) {
      setStatusMessage(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h1>Chequebook Reorder Alert Service</h1>

        <label>Account Number</label>
        <input
          type="text"
          value={acctNo}
          onChange={(e) => setAcctNo(e.target.value)}
        />
        <button onClick={verifyAccount} disabled={loading}>
          Verify
        </button>

        <p>{statusMessage}</p>

        <input type="text" value={acctName} readOnly />
        <input type="text" value={branch} readOnly />

        <input
          type="number"
          value={threshold}
          onChange={(e) => setThreshold(e.target.value)}
          disabled={!verified}
        />

        <label>
          <input
            type="checkbox"
            checked={notifySMS}
            onChange={() => setNotifySMS(!notifySMS)}
          />
          SMS
        </label>

        <label>
          <input
            type="checkbox"
            checked={notifyEmail}
            onChange={() => setNotifyEmail(!notifyEmail)}
          />
          Email
        </label>

        <label>
          <input
            type="checkbox"
            checked={notifyRM}
            onChange={() => setNotifyRM(!notifyRM)}
          />
          RM
        </label>

        <div className="actions">
          {!subscribed ? (
            <button
              onClick={() => handleSubscribeOrUpdate("subscribe")}
              disabled={loading}
            >
              Subscribe
            </button>
          ) : (
            <>
              <button
                onClick={() => handleSubscribeOrUpdate("update")}
                disabled={loading}
              >
                Update
              </button>
              <button onClick={handleUnsubscribe} disabled={loading}>
                Unsubscribe
              </button>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Subscription;
